
module vga_top (
    input  wire       clk_in,
    input  wire       reset,
    input  wire [8:0] sw, // Use a 9-bit switch input for 9 cells
    output wire       VGA_HS,
    output wire       VGA_VS,
    output wire [3:0] VGA_R,
    output wire [3:0] VGA_G,
    output wire [3:0] VGA_B
);

    // 25MHz clock for VGA timing, generated by a simple counter
    wire clk25, clk100;
    wire locked;
    
    clk_wiz_0 u_clk25 (
        .clk_in    (clk_in),
        .reset  (reset),
        .clk_25M   (clk25),
        .clk_100M  (clk100),
        .locked (locked)
        
    );

    // VGA sync signals and coordinates
    wire [9:0] x;
    wire [9:0] y;
    wire       en;
    vga_sync u_sync (
        .clk      (clk25),
        .reset    (reset), // Use the main reset directly
        .x        (x),
        .y        (y),
        .hsync    (VGA_HS),
        .vsync    (VGA_VS),
        .en       (en)
    );

    // Graphics generator
    vga_graphics u_grp (
        .clk      (clk25),
        .reset    (reset), // Use the main reset directly
        .x        (x),
        .y        (y),
        .en       (en),
        .sw       (sw),
        .red      (VGA_R),
        .green    (VGA_G),
        .blue     (VGA_B)
    );

endmodule
